#' Set working directory
setwd('/mnt/data/asis/untwist/')

#' Load transposed accession coordinates generated by our principal component
#' analysis:
unt.coords <- read.table('results/public_and_untwist_bcftools_and_plink_filtered_pca_matrix.rel', stringsAsFactors=FALSE)
#' Read and set the row (accession) identifier. Clean them, because PLINK had
#' to duplicate them, see plink --double-id
unt.accession.names <- sub("_CAM.*$", "", readLines('results/public_and_untwist_bcftools_and_plink_filtered_pca_matrix.rel.id'))
rownames(unt.coords) <- unt.accession.names

